import React, { useState } from "react";
import './CSS/LoginSignup.css';
const LoginSignup = () => {
    const [state,setState] = useState("Signup");
    const login = async()=>{
        console.log('Login Function Executed');
    }
    const signup = async ()=>{
        console.log("Signup Function Executed");
    }
    return (
        <div className="loginsignup">
            <div className="loginsignup-container">
                <h1>Sign Up</h1>
                <div className="loginsignup-fields">
                    {state === "Signup"?<input type="text" placeholder="Your Name"/>:<></>}
                    <input type="email" placeholder="Email Address"/>
                    <input type="password" placeholder="Password"/>
                </div>
                <button on>Continue</button>
                <p className="loginsignup-login" >Already have an account? <span onClick={()=>{setState('Login')}}>Login here</span></p>
                <p className="loginsignup-login" >Create an account? <span onClick={()=>{setState('Signup')}}>Signup here</span></p>
                <div className="loginsignup-agree">
                    <input type="checkbox" name="" id=""></input>
                    <p>By continuing, i agree to the terms of use & privacy policy. </p>
                </div>
            </div>

        </div>
    )
}

export default LoginSignup;
